document.addEventListener("DOMContentLoaded", () => {
    const form = document.getElementById("formServicios");
    const inputs = form.querySelectorAll("input, select, textarea");
    const mensajeError = document.getElementById("mensajeError");
    const btnFinalizar = document.getElementById("btnFinalizar");

    // Recuperar datos guardados en localStorage
    inputs.forEach(input => {
        const savedValue = localStorage.getItem(input.id);
        if (savedValue) input.value = savedValue;
    });

    // Validaci√≥n en tiempo real
    inputs.forEach(input => {
        input.addEventListener("input", () => {
            localStorage.setItem(input.id, input.value.trim());
            validarFormulario();
        });

        input.addEventListener("keypress", (e) => {
            if (e.key === "Enter") {
                e.preventDefault();
                const formElements = Array.from(inputs);
                const index = formElements.indexOf(input);
                if (index >= 0 && index < formElements.length - 1) {
                    formElements[index + 1].focus();
                }
            }
        });
    });

    function validarFormulario() {
        let valido = true;
        mensajeError.textContent = "";

        inputs.forEach(input => {
            if (input.hasAttribute("required") && input.value.trim() === "") {
                valido = false;
            }
        });

        if (!valido) {
            mensajeError.textContent = "‚ö†Ô∏è Completa todos los campos obligatorios.";
        }

        btnFinalizar.disabled = !valido;
    }

    // Env√≠o del formulario y generaci√≥n de PDF
    form.addEventListener("submit", (e) => {
        e.preventDefault();

        let valido = true;
        inputs.forEach(input => {
            if (input.hasAttribute("required") && input.value.trim() === "") {
                valido = false;
            }
        });

        if (!valido) {
            mensajeError.textContent = "‚ö†Ô∏è Completa todos los campos obligatorios.";
            return;
        }

        // Crear PDF despu√©s de la validaci√≥n
        const { jsPDF } = window.jspdf;
        const doc = new jsPDF();

        const data = {
            empresa: document.getElementById("empresa").value,
            tipoServicio: document.getElementById("tipoServicio").value,
            publico: document.getElementById("publico").value,
            automatizar: document.getElementById("automatizar").value,
            infoCliente: document.getElementById("infoCliente").value,
            pasosPrevios: document.getElementById("pasosPrevios").value,
            reservas: document.getElementById("reservas").value,
            fotos: document.getElementById("fotos").value,
            metodosPago: document.getElementById("metodosPago").value,
            politica: document.getElementById("politica").value,
            faq: document.getElementById("faq").value,
            canales: document.getElementById("canales").value
        };

        // üñºÔ∏è LOGO CENTRADO
        const logoBase64 = 'data:image/png;base64,';
        doc.addImage(logoBase64, 'PNG', 85, 10, 40, 25); // Centrado: x=85 (centro), m√°s grande

        // üìã T√çTULO CENTRADO DEBAJO DEL LOGO
        doc.setFontSize(18);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(0, 0, 0); // Negro
        doc.text('FORMULARIO', 105, 45, { align: 'center' }); // M√°s abajo del logo

        // üìù SUBT√çTULO
        doc.setFontSize(12);
        doc.setFont('helvetica', 'normal');
        doc.setTextColor(100, 100, 100); // Gris
        doc.text('Servicios', 105, 55, { align: 'center' });
        let y = 70;
        const lineHeight = 10;
        const pageHeight = doc.internal.pageSize.height;
        const bottomMargin = 20;



        // Objeto con preguntas y sus respuestas
        const preguntas = {
            "¬øCu√°l es el nombre de tu empresa?": data.empresa,
            "¬øQu√© tipo de servicios brindas?": data.tipoServicio,
            "¬øCu√°l es tu p√∫blico objetivo principal?": data.publico,
            "¬øQu√© procesos o tareas te gustar√≠a automatizar?": data.automatizar,
            "¬øQu√© informaci√≥n del cliente necesitas para dar el servicio?": data.infoCliente,
            "¬øTu cliente necesita seguir pasos previos?": data.pasosPrevios,
            "¬øLos clientes necesitan reservar una cita?": data.reservas,
            "¬øTienes fotos de tus servicios listas para subir?": data.fotos,
            "¬øQu√© m√©todos de pago aceptas?": data.metodosPago,
            "¬øCu√°l es tu pol√≠tica de servicio? (cancelaciones, reembolsos, etc.)": data.politica,
            "¬øQu√© dudas frecuentes tienen tus clientes antes de contratar?": data.faq,
            "¬øEn qu√© canales brindas soporte actualmente?": data.canales
        };
    

        for (const [pregunta, respuesta] of Object.entries(preguntas)) {
            // Verifica si hay espacio suficiente para la siguiente pregunta
            // La altura de cada entrada es de 2 l√≠neas (pregunta + respuesta)
            if (y + lineHeight * 2 > pageHeight - bottomMargin) {
                doc.addPage();
                y = 30; // Reinicia la posici√≥n vertical para la nueva p√°gina con un margen superior
            }

            // Dibuja la pregunta y la respuesta
            doc.setFontSize(12);
            doc.setTextColor(52, 58, 64);
            doc.text(pregunta, 20, y);

            doc.setFontSize(10);
            doc.setTextColor(100, 100, 100);
            doc.text(respuesta, 20, y + lineHeight);

            y += lineHeight * 2; 
        }


        doc.save("formulario_servicios.pdf");

        localStorage.clear();
        form.reset();
        btnFinalizar.disabled = true;

        setTimeout(() => {
            window.location.href = "final.html";
        }, 500);
    });
});